from .ball_detection_utils import detect_ball_BGR_hough_circles
from .BallDetector import BallDetector

# detector class that doesn't implement memory (our original implementation). 
# This class corresponds to the detect_ball_BGR_hough_circles algorithm.
class NaiveBallDetector(BallDetector):
    def __init__(self, camera = -1):
        self.center = (720, 540)
        self.camera = camera

    def next_position(self, frame, show_images = False):
        new_center, new_radius = detect_ball_BGR_hough_circles(frame)
        return new_center, new_radius